import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

const HelpModal = () => {
  const [isOpen, setIsOpen] = useState(false);

  const showModal = () => {
    setIsOpen(true);
  };

  const hideModal = () => {
    setIsOpen(false);
  };

  return (
    <div className="helpModal">
      <Button variant="secondary" onClick={showModal}>
        Help
      </Button>

      <Modal show={isOpen} onHide={hideModal}>
        <Modal.Header>
          <Modal.Title>Instructions:</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <ul>
            <li>
              <h2 className="lead">Search for your favorite content!</h2>
            </li>
            <li>
              <h3 className="lead">Enter a search term and select a category.</h3>
            </li>
            <li>
              <h3 className="lead">Click 'Search' to view the results!</h3>
            </li>
            <li>
              <h3 className="lead">
                To add an item to your favorites, click the 'star' icon on the item's card.
              </h3>
            </li>
          </ul>
          <h3 className="lead text-center">And finally... Enjoy!</h3>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={hideModal}>Return</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default HelpModal;
